function Cmd() {
		
	var _layer = 'MAIN_LAYER';
	var _controlContainer = 'control';
	
	this.setLayer  = function(layer) {
		_layer = layer;
		return this;
	};	
	
	// will construct and return the actual command
	this.getCommandString = function() {
		return this.getNextCount() + ' RENDERER*' + _layer + '*TREE*$' + _controlContainer + '\0'; 	
	};
	
	this.setControlContainer = function(controlContainer) {
		_controlContainer = controlContainer;
		return this;
	};	
};

Cmd.prototype.getConstants = function() {
	return {
		MAIN_LAYER: "MAIN_LAYER",
		FRONT_LAYER: "FRONT_LAYER"
	};	
};

// closure magic
Cmd.prototype.getNextCount = (function() {
	var id = 0; 
	return function() { 
		return id++; 
	};  // return and increment
})();

// factory to create a Cmd instance
exports.create = function() {
	return new Cmd();
};



