exports.create = function() {
	
	var self = this;	
	
	// returns itself to fulfill the fluent approach (I guess)
	self.addLayer = function(layer) {		
		self.layer = layer;
		return self;
	}
	return self;	
};


exports.constants = function() {

	this.main_layer = "MAIN_LAYER";	
	return this;	
};

