// factory to create an anonymous instance
exports.createCommand = function() {
		
	var _layer = 'MAIN_LAYER';
	var _controlContainer = 'control';
	
	return {
		setLayer : function(layer) {
			_layer = layer;
			return this;
		},
		setControlContainer : function(controlContainer) {
			_controlContainer = controlContainer;
			return this;
		},
		getCommandString : function() {
			return staticCommandCountUp() + ' RENDERER*' + _layer + '*TREE*$' + _controlContainer + '\0'; 	
		}
	};
};


// closure magic
var staticCommandCountUp = (function() {
	var id = 0; 
	return function() { 
		return id++; 
	};  // return and increment
})();

// constants
exports.layers = {
	BACK_LAYER: "BACK_LAYER",
	MAIN_LAYER: "MAIN_LAYER",
	FRONT_LAYER: "FRONT_LAYER"
};